import React, { Component } from 'react';
import {
    StyleSheet,
    FlatList,
    View,
    Text,
    Image,
    Platform,
    TouchableWithoutFeedback
} from 'react-native';
import { shadowgiver } from "../../util/helpers";
import { star, half_star } from "../../assets/icons";

interface product {
    id: string;
    name: string;
    image: string;
    category?: { id: number, name: string };
    ratings: { avg: number, total: number };
    pricing: number;
    currencyCode: string;
};

type Props = {
    width: number;
    callback?: any;
    height: number;
    style?: any;
    data: product;
};
type State = {};

export default class ProductCard extends React.PureComponent<Props, State> {

    static whyDidYouRender = true

    getRatings(avg: number) {
        let out = [];
        const stars = Math.round((avg) * 2) / 2;
        // console.log(avg);
        for (let i = 1; i <= stars; i++) {
            out.push(
                <View key={i}>
                    {star("#ffbb58", 12.4, { marginRight: 1 })}
                </View>
            );
            if (stars - i === 0.5) {
                out.push(
                    <View key={i + 1}>
                        {half_star("#ffbb58", 12.4, { marginRight: 1 })}
                    </View>
                );
            }
        }
        return (
            <View style={{ flex: 1, flexDirection: "row", alignItems: "center" }}>{out}</View>
        );
    }
    render() {
        const { data } = this.props;

        return (
            <View style={this.props.style}>
                <TouchableWithoutFeedback onPress={this.props.callback}>
                    <View style={[styles.mContainer, { width: this.props.width, minHeight: this.props.height }]}>
                    <Image source={{ uri: this.props.data.image.replace(/^(\/\/\.*?)/i, "https://") }} style={{ height: 181.25, backgroundColor: "rgb(199,199, 205)" }} />
                    <View style={{
                        flex: 1, backgroundColor: "white",
                        padding: 5,
                        paddingRight: 7
                    }}>
                        {data.category && (<Text style={styles.category}>{data.category.name}</Text>)}
                        <Text style={styles.item_name}>{data.name}</Text>

                        <View style={styles.product_status_container}>
                            <View style={{ alignSelf: "flex-end" }}>
                                <View style={styles.ratingContainer}>
                                    {data.ratings.total === 0 && (
                                        <React.Fragment>
                                            <View>{this.getRatings(1)}</View>
                                            <Text style={styles.newly_text}>Newly Arrived</Text>
                                        </React.Fragment>
                                    )}
                                    {data.ratings.total !== 0 && (
                                        <React.Fragment>
                                            <Text style={styles.rating_avg}>{data.ratings.avg}</Text>
                                            <View>{this.getRatings(data.ratings.avg)}</View>
                                            <Text style={styles.total_rating_text}> ( {data.ratings.total} )</Text>
                                        </React.Fragment>
                                    )}

                                </View>
                            </View>
                            <View style={styles.booking_price_container}>
                                <Text style={styles.booking_price_desc}>from</Text>
                                <Text style={styles.booking_price}>{data.currencyCode === "USD" ? "$" : data.currencyCode}{data.pricing}</Text>
                            </View>
                        </View>
                    </View>
                </View>
            </TouchableWithoutFeedback>
            </View >
        );
    }
}

const styles = StyleSheet.create({
    mContainer: {
        flex: 1,
        marginTop: 3,
        flexGrow: Platform.OS !== "web" ? 0 : 1,
        marginLeft: 3,
        marginBottom: 5,
        backgroundColor: "white",
        marginRight: 10,
        zIndex: 12,
        ...shadowgiver(3, "#000", 5, 10)
    },
    image: {
        flex: 0.3
    },
    category: {
        marginLeft: 5,
        fontSize: 12.09,
        color: "rgba(86,86,86, 0.4)",
        marginTop: 5,
        textTransform: "uppercase",
        fontWeight: "900",
        fontFamily: "Avenir"
    },
    item_name: {
        fontSize: 15,
        marginTop: 2.22,
        color: "rgb(84,84,84)",
        fontFamily: "Avenir",
        lineHeight: 23,
        marginLeft: 5
    },
    ratingContainer: {
        marginTop: 5,

        flexDirection: "row"
    },
    rating_avg: {
        fontSize: 14,
        paddingRight: 5.6,
        // fontWeight:"bold",
        color: "rgb(255, 187, 88)",
        fontFamily: "Avenir"
    },
    newly_text: {
        fontSize: 12,
        color: "rgb(186, 186, 186);",
        fontFamily: "Avenir",
        marginLeft: 4,
    },
    total_rating_text: {
        fontSize: 12,
        color: "rgb(186, 186, 186);",
        fontFamily: "Avenir",
    },
    booking_text: {
        fontSize: 14,
        color: "#d8d8d8",
        fontFamily: "Avenir",
    },
    booking_price_container: {
        alignSelf: "flex-end",
        marginRight: 4
    },
    booking_price_desc: {
        fontSize: 12,
        alignSelf: "flex-end",
        color: "rgb(154, 154, 154);",
        fontFamily: "Avenir",
    },
    booking_price: {
        fontSize: 18,
        marginTop: 2,
        textAlign: "right",
        color: "rgb(84,84,84)",
        fontWeight: "500",
        fontFamily: "Avenir",
    },
    product_status_container: {
        flex: 1,
        // flexGrow: 0.5,
        flexDirection: "row",
        marginLeft: 5,
        position: "relative",
        marginTop: 24,
        top: -2,
        justifyContent: "space-between"
    },
    container: {
        backgroundColor: "white",
        marginBottom: 10,
        marginTop: 10,
        marginLeft: 5
    }
});
